mapper.select:
SELECT itemno, title, price, content, thumb, categoryno
FROM item
ORDER BY itemno DESC;

SELECT itemno, title, content, thumb, price, categoryno
FROM item
WHERE itemno=#{itemno}

mapper.update:
UPDATE item
SET title=#{title}, content=#{content}, price=#{price}, thumb=#{thumb}, categoryno=#{categoryno}
WHERE itemno=#{itemno}
